import jsyaml from 'js-yaml'
import {initLoadingBar, initSplash, loadStyle, preparePath} from './utils';
import RJSState from './RJSState';
import {Sprite} from 'phaser-ce';
import PreloadStory from './PreloadStory';
import RJSGUIByBuilder from '../gui/RJSGUIByBuilder';
import RJSSimpleGUI from '../gui/RJSSimpleGUI';
import RJS from '../core/RJS';
import RJSLoadingScreen from '../components/RJSLoadingScreen';


class Boot extends RJSState {
    // loadingScreen:RJSLoadingScreen
    splash: Sprite
    constructor() {
        super();
    }

    init(): void {
        if (!this.game.config.i18n) this.game.setupScreen();
        // create renjs splash screen and preload loading screen and story
        const b64Splash = 'data:image/png;base64,';
        this.game.load.image("renJSSplashScreen", b64Splash);
        this.game.load.onLoadComplete.addOnce(() => {
            this.splash = this.game.add.sprite(this.game.world.centerX, this.game.world.centerY, 'renJSSplashScreen');
            this.splash.alpha = 0;
            this.splash.anchor.set(0.5);
            this.game.add.tween(this.splash).to({alpha:1}, 1500, null,true,0,-1,true);
        }, this);
        this.game.load.start();
    }

    preload (): void {
        if (this.game.config.splash.loadingScreen){
            this.game.load.image('loadingScreenBg',  preparePath(this.game.config.splash.loadingScreen, this.game));
        }
        if (this.game.config.splash.loadingBar) {
            if (this.game.config.splash.loadingBar.fullBar){
                this.game.load.image('loadingScreenBar',  preparePath(this.game.config.splash.loadingBar.fullBar, this.game));
            }
            if (this.game.config.splash.loadingBar.asset){
                const w = this.game.config.splash.loadingBar.size.w;
                const h = this.game.config.splash.loadingBar.size.h;
                this.game.load.spritesheet('loadingScreenBar',  preparePath(this.game.config.splash.loadingBar.asset, this.game),w,h);
            }
        }
        loadStyle(preparePath(this.game.config.fonts, this.game));
        this.game.load.text('guiConfig', preparePath(this.game.config.guiConfig, this.game));
        this.game.load.text('storySetup', preparePath(this.game.config.storySetup, this.game));
        this.game.load.text('storyConfig', preparePath(this.game.config.storyConfig, this.game));
        for (let i = this.game.config.storyText.length - 1; i >= 0; i--) {
            this.game.load.text('story'+i, preparePath(this.game.config.storyText[i], this.game));
        }
    }

    create (game: RJS): void {
        this.input.onDown.addOnce(()=> {
            if (this.sound.context.state === 'suspended') {
                this.sound.context.resume();
            }
        });
        // load the setup
        game.setup = jsyaml.load(game.cache.getText('storySetup'));
        if (!game.setup) game.setup = {};
        game.storyConfig = jsyaml.load(game.cache.getText('storyConfig'));
        // load the story text
        const story = {};
        game.config.storyText.forEach((file,index) => {
            const text = jsyaml.load(game.cache.getText('story' + index));
            Object.assign(story, {...text})
        })

        game.story = story;
        // load and create the GUI
        game.guiSetup = jsyaml.load(game.cache.getText('guiConfig'));
        if (game.guiSetup.madeWithRenJSBuilder){
            game.gui = new RJSGUIByBuilder(game)
        } else {
            game.gui = new RJSSimpleGUI(game)
        }
        // preload the fonts by adding text, else they wont be fully loaded :\
        for (const font of game.gui.fonts){
            game.add.text(20, -100, font, {font: '42px ' + font});
        }
        game.state.add('preloadStory', PreloadStory);
        game.state.start('preloadStory');
    }
}

export default Boot
